import axios from 'axios';
import useSWR from 'swr';


const loginUser = async (url,email, password) => {
	// Call an external API endpoint to get posts
    const res = await fetch(url, {
		method: 'POST',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			email: email,
			password: password
	 	})
	});

    const user = await res.json();
	return user;
}

export function useUserLogin (payload) {
	const url = "http://practxbestaging-env.eba-6m7puu5w.us-east-2.elasticbeanstalk.com/api/auth/root/login";
  	const { data, error } = useSWR([url, payload.email, payload.password], loginUser)
	return {
	    user: data.root,
	    isLoading: !error && !data,
		isError: error,
		token: data.token
	}
}
