	return data;
}













export const loginPractice = async (payload) => {//--------------------------- LOGIN PRACTICE -------------

    const res = await fetch("http://practxbestaging-env.eba-6m7puu5w.us-east-2.elasticbeanstalk.com/api/auth/practices/login", {
		method: 'POST',
