import * as t from "../constants";

const initialState = {
	user:{},
	token:""
};

function authReducer (state = initialState, action){
	switch(action.type){
		case t.ROOT_USER_LOGIN:
			state = {...state, token:action.payload.token, user:action.payload.root}
			return state;

		case t.USER_LOGOUT:
			state = {...state, token:"", user:{}}
			return state;
		default:
			return state;
	}
}

export default authReducer;
