import {useEffect, useState} from 'react';
import {Media} from 'reactstrap';

import styles from './Messageblob.module.scss';

import { Email, Phone } from '@styled-icons/material';


export default function Messageblob({text, index}) {

	const side = index == 1 ? styles.chatmessage2 : styles.chatmessage1;

    return (
		<Media className={side}>

			<Media body>

				<div className={styles.message}>

						<span>
							{text}
						</span>

				</div>

				<span className={styles.messagetime}>
					A few minutes ago
				</span>
			</Media>

			<Media left href="#" className={styles.profilepic}>

				<img src="/images/users/avatar-5.jpg" alt="Group thumbnail"/>

			</Media>
		</Media>

    )
}
