import { call, put, takeEvery, takeLatest } from 'redux-saga/effects';
import * as t from "../constants";
import * as Api from '../Api.js';

// worker Saga: will be fired on USER_FETCH_REQUESTED actions
function* loginRootUser(action) {
   try {
		const user = yield call(Api.useUserLogin, action.payload);
    	yield put({type: t.ROOT_USER_LOGIN, user: user.user, token:user.token});
   } catch (e) {
	   console.log("Can not login from saggaggaggag")
   }
}

function* mySaga() {
  yield takeLatest("RLOGIN", loginRootUser);
}

export default mySaga;
