import { call, put, takeEvery, takeLatest, all, fork} from 'redux-saga/effects';
import * as t from "./constants";
import * as Api from '../Api.js';
import * as Actions from "./actions";



/*------------------------LOGIN SAGA --------------------------------*/

function* loginRootUser(action) {

   try {

		const user = yield call(Api.loginRootUser, action.payload);


    if (user.message === "Login successful!"){


        yield put(Actions.loginRootUserSuccess(user.root, user.token ));

        yield call(()=>{

                action.payload.router.push({
                  pathname:"/dashboard/dashboard"
                })

            }
        );

      }else{

          yield call(()=>{

                  action.payload.router.push({
                    pathname:"/"
                  })

              }
          );
      }


   } catch (e) {
	   console.log(e, "Can not login from saggaggaggag")
   }
}

function* rootLoginWatcher() { /* --------------- LOGIN WATCHER ---------------------------*/

  yield takeLatest("ROOT_LOGIN_WATCHER", loginRootUser);

}



/*------------------------LOGOUTSAGA --------------------------------*/

function* logoutRootUser() {

    yield put({type: t.ROOT_USER_LOGOUT});
}

function* logoutWatcher() { /* --------------- LOGIN WATCHER ---------------------------*/

  yield takeLatest("LOGIN", loginRootUser);

}


function* authSaga() {
    yield all([
        fork(rootLoginWatcher),
        fork(logoutWatcher),
    ]);
}


export default authSaga;
